import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, TouchableOpacity, KeyboardAvoidingView, Image } from 'react-native';
import { Button, TextInput } from 'react-native-paper';

// <View style={styles.container}>
export default function SignIn() {
    return (
        <>
            <KeyboardAvoidingView behavior="position">

                <StatusBar style="light" backgroundColor="black" barStyle="light-content" />
                <Image style={styles.logoStyle} source={require('../assets/logoBlack.png')} />


                <Text style={styles.subIntro}>Proceed with your</Text>
                <Text style={styles.intro}> Login</Text>


                <TextInput style={styles.textInpStyle} theme={{ colors: { primary: "red" } }}
                    underlineColor=""

                    label="Email" mode="flat" />
                <TextInput style={styles.textInpStyle} theme={{ colors: { primary: "red" } }}
                    label="Password" mode="flat" />
                <Button style={styles.btnStyle} mode="contained" onPress={() => console.log('Pressed')}>
                    SIGN IN
  </Button>
                <TouchableOpacity>
                    <Text style={styles.subText} >I'm a new user <Text style={styles.spIn}> Sign Up</Text> </Text>
                </TouchableOpacity>

            </KeyboardAvoidingView>

        </>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    intro: {
        fontSize: 30,
        marginLeft: 15,
        marginTop: 10,
        color: "#3b3b3b",
        fontWeight: "bold"
    },
    spIn: {
        fontWeight: "bold",
    
        color: "blue"
    },
    subIntro: {
        marginTop: 10,
        fontSize: 20,
        marginLeft: 18,
        color: "black"
    },
    selfClosing: {
        borderBottomColor: "red",
        borderBottomWidth: 2,
        marginLeft: 120,
        marginRight: 100,
        marginTop: 0,
        marginBottom: 5
    },
    txtLabel: {
        marginTop: 10,
        marginLeft: 15,
    },
    titleM: {
        fontSize: 30,
        marginLeft: 18,
        marginTop: 15,
        textAlign: "center"
    },
    bckBtn: {
        backgroundColor: "white"
    },
    bckBtnHead: {
        backgroundColor: "white",
        borderBottomColor: "white"
    },
    textInpStyle: {
        marginLeft: 18,
        marginRight: 18,
        marginTop: 10,
        backgroundColor: "white"
    },
    btnStyle: {
        marginLeft: 18,
        marginRight: 18,
        marginTop: 30,
        backgroundColor: "black",
        textShadowColor: "white",
        borderRadius: 35,
    },
    subText: {
        fontSize: 15,
        marginLeft: 18,
        marginTop: 5,
        textAlign: "center"
    },
    logoStyle: {
        marginLeft: 145,
        marginTop: 100,
        height: 140,
        width: 130,
        marginBottom: 20
    }
});
